//SPDX-License-Identifier:MIT
pragma solidity ^0.8.20;
import {Test_Base} from "./Test_Base.t.sol";
import {MockDistribution} from "test/Mocks/MockDistribution.sol";
import {RecipientHandler} from "contracts/RecipientHandler.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Test_Claim is Test_Base{

    // Helper function to create a valid order and execute payment
    function executeValidPayment() internal returns (uint256 sellerProfit) {
        RecipientHandler.Order memory order = RecipientHandler.Order({
            buyer: buyer,
            payTo: address(handler),
            paymentToken: USDC,
            totalAmount: price,
            nonce: handler.nextNonce(buyer),
            quoteId: quoteId,
            serviceId: serviceId,
            deadline: nowTime + 1 hours
        });

        RecipientHandler.PriceQuote memory quote = RecipientHandler.PriceQuote({
            quoteId: quoteId,
            paymentToken: USDC,
            seller: seller,
            price: price,
            cost: cost,
            serviceId: serviceId,
            expiry: nowTime + 1 hours
        });

        bytes32 orderDigest = hashTyped(
            keccak256(
                abi.encode(
                    ORDER_TYPEHASH,
                    order.buyer,
                    order.payTo,
                    order.paymentToken,
                    order.totalAmount,
                    order.nonce,
                    order.quoteId,
                    order.serviceId,
                    order.deadline
                )
            )
        );

        bytes32 quoteDigest = hashTyped(
            keccak256(
                abi.encode(
                    PRICEQUOTE_TYPEHASH,
                    quote.quoteId,
                    quote.paymentToken,
                    quote.seller,
                    quote.price,
                    quote.cost,
                    quote.serviceId,
                    quote.expiry
                )
            )
        );

        bytes memory buyerSig = sign(orderDigest, buyerPk);
        bytes memory sellerQuoteSig = sign(quoteDigest, sellerPk);

        vm.prank(buyer);
        handler.payWithEIP712(
            order,
            buyerSig,
            quote,
            sellerQuoteSig,
            "",
            ""
        );

        // Calculate expected seller profit
        uint256 netProfit = price - cost;
        uint256 commission = (netProfit * commissionRate) / BPS;
        sellerProfit = price - commission;
    }



    function test_Claim_Success() public {
        uint256 expectedProfit = executeValidPayment();

        uint256 sellerBalanceBefore = IERC20(USDC).balanceOf(seller);
        uint256 handlerBalanceBefore = IERC20(USDC).balanceOf(address(handler));

        vm.prank(seller);
        vm.expectEmit(true, true, false, true);
        emit RecipientHandler.SellerClaimed(seller, USDC, expectedProfit);
        handler.claim(USDC, expectedProfit);

        uint256 sellerBalanceAfter = IERC20(USDC).balanceOf(seller);
        uint256 handlerBalanceAfter = IERC20(USDC).balanceOf(address(handler));

        assertEq(sellerBalanceAfter - sellerBalanceBefore, expectedProfit, "Seller should receive correct amount");
        assertEq(handlerBalanceBefore - handlerBalanceAfter, expectedProfit, "Handler balance should decrease");
        assertEq(handler.getProfit(seller, USDC), 0, "Seller profit should be zero after claim");
    }

    function test_Claim_PartialClaim() public {
        uint256 totalProfit = executeValidPayment();
        uint256 partialAmount = totalProfit / 2;

        vm.prank(seller);
        handler.claim(USDC, partialAmount);

        assertEq(handler.getProfit(seller, USDC), totalProfit - partialAmount, "Remaining profit should be correct");

        // Claim the rest
        vm.prank(seller);
        handler.claim(USDC, totalProfit - partialAmount);

        assertEq(handler.getProfit(seller, USDC), 0, "All profit should be claimed");
    }

    function test_Claim_MultipleClaims() public {
        // Execute multiple payments
        executeValidPayment();

        quoteId = keccak256("q-2");
        executeValidPayment();

        quoteId = keccak256("q-3");
        uint256 expectedProfit = executeValidPayment();

        uint256 totalProfit = handler.getProfit(seller, USDC);
        assertEq(totalProfit, expectedProfit * 3, "Total profit should be sum of all payments");

        vm.prank(seller);
        handler.claim(USDC, totalProfit);

        assertEq(handler.getProfit(seller, USDC), 0, "All profit should be claimed");
    }

    function test_Claim_RevertZeroAddress() public {
        executeValidPayment();

        vm.prank(seller);
        vm.expectRevert();
        handler.claim(address(0), 100e6);
    }

    function test_Claim_RevertZeroAmount() public {
        executeValidPayment();

        vm.prank(seller);
        vm.expectRevert();
        handler.claim(USDC, 0);
    }

    function test_Claim_RevertInsufficientBalance() public {
        uint256 profit = executeValidPayment();

        vm.prank(seller);
        vm.expectRevert();
        handler.claim(USDC, profit + 1);
    }

    function test_Claim_RevertNoProfit() public {
        vm.prank(seller);
        vm.expectRevert();
        handler.claim(USDC, 1e6);
    }

    function test_Claim_DifferentSellers() public {
        // First seller payment
        uint256 profit1 = executeValidPayment();

        // Create second seller
        uint256 seller2Pk = 789;
        address seller2 = vm.addr(seller2Pk);
        uint128 serviceId2 = 1002;

        vm.prank(owner);
        handler.createService(serviceId2, seller2);

        vm.prank(seller2);
        handler.setServiceActive(serviceId2);

        // Second seller payment
        RecipientHandler.Order memory order = RecipientHandler.Order({
            buyer: buyer,
            payTo: address(handler),
            paymentToken: USDC,
            totalAmount: price,
            nonce: handler.nextNonce(buyer),
            quoteId: keccak256("q-seller2"),
            serviceId: serviceId2,
            deadline: nowTime + 1 hours
        });

        RecipientHandler.PriceQuote memory quote = RecipientHandler.PriceQuote({
            quoteId: keccak256("q-seller2"),
            paymentToken: USDC,
            seller: seller2,
            price: price,
            cost: cost,
            serviceId: serviceId2,
            expiry: nowTime + 1 hours
        });

        bytes32 orderDigest = hashTyped(
            keccak256(
                abi.encode(
                    ORDER_TYPEHASH,
                    order.buyer,
                    order.payTo,
                    order.paymentToken,
                    order.totalAmount,
                    order.nonce,
                    order.quoteId,
                    order.serviceId,
                    order.deadline
                )
            )
        );

        bytes32 quoteDigest = hashTyped(
            keccak256(
                abi.encode(
                    PRICEQUOTE_TYPEHASH,
                    quote.quoteId,
                    quote.paymentToken,
                    quote.seller,
                    quote.price,
                    quote.cost,
                    quote.serviceId,
                    quote.expiry
                )
            )
        );

        bytes memory buyerSig = sign(orderDigest, buyerPk);
        bytes memory seller2QuoteSig = sign(quoteDigest, seller2Pk);

        vm.prank(buyer);
        handler.payWithEIP712(order, buyerSig, quote, seller2QuoteSig, "", "");

        // Both sellers claim
        vm.prank(seller);
        handler.claim(USDC, profit1);

        vm.prank(seller2);
        handler.claim(USDC, profit1);

        assertEq(handler.getProfit(seller, USDC), 0);
        assertEq(handler.getProfit(seller2, USDC), 0);
    }

}

test/Test_PayWithEIP712.t.sol.content:
//SPDX-License-Identifier:MIT
pragma solidity ^0.8.20;

import {Test_Base} from "./Test_Base.t.sol";
import {Test} from "lib/forge-std/src/Test.sol";
import {MockDistribution} from "test/Mocks/MockDistribution.sol";
import {RecipientHandler} from "contracts/RecipientHandler.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Nonces} from "@openzeppelin/contracts/utils/Nonces.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "contracts/libraries/Errors.sol";

contract Test_PayWithEIP712 is Test, Nonces {
    address constant USDC = 0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913;

    uint256 buyerPk = 0xB0B;
    uint256 sellerPk = 0xC0C;
    address buyer;
    address seller;

    string constant NAME = "Test";
    string constant VERSION = "1";

    uint128 serviceId = 1001;
    uint256 commissionRate = 1000; // 10%
    uint256 price = 1_000e6; // 1000 USDC
    uint256 cost = 100e6; // 100  USDC

    bytes32 quoteId;
    uint64 nowTime;

    uint256 forkId;

    MockDistribution distribution;
    RecipientHandler handler;

    function setUp() public {
        forkId = vm.createSelectFork(
            "https://base-mainnet.infura.io/v3/c8b8880b688449e098c268f568bf7700"
        );
        //Generate address
        buyer = vm.addr(buyerPk);
        seller = vm.addr(sellerPk);

        distribution = new MockDistribution();
        handler = new RecipientHandler(
            "Test",
            "1",
            address(distribution),
            1_000
        );
        handler.createService(serviceId, seller);
        vm.prank(seller);
        handler.setServiceActive(serviceId);
        deal(USDC, buyer, 100_000e6);
        deal(USDC, address(handler), 100_000e6);
        vm.startPrank(buyer);
        IERC20(USDC).approve(address(handler), type(uint256).max);
        vm.stopPrank();
        vm.deal(buyer, 100 ether);
        vm.deal(seller, 100 ether);

        nowTime = uint64(block.timestamp);
        quoteId = keccak256("q-1");
    }

    // typehash
    bytes32 constant ORDER_TYPEHASH =
        keccak256(
            "Order(address buyer, address payTo,address paymentToken, uint256 totalAmount, uint256 nonce,bytes32 quoteId, uint128 serviceId, uint64 deadline)"
        );
    bytes32 constant PRICEQUOTE_TYPEHASH =
        keccak256(
            "PriceQuote(bytes32 quoteId,address paymentToken,address seller,uint256 price,uint256 cost,uint128 serviceId,uint64 expiry)"
        );

    bytes32 constant EIP712_DOMAIN_TYPEHASH =
        keccak256(
            "EIP712Domain(string name,string version, uint256 chainId, address verifyingContract)"
        );

    function domainSeparatorLocal() internal view returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    EIP712_DOMAIN_TYPEHASH,
                    keccak256(bytes("Test")),
                    keccak256(bytes("1")),
                    8453, //Base mainnet
                    address(handler)
                )
            );
    }

    function hashTyped(bytes32 structHash) internal view returns (bytes32) {
        return
            keccak256(
                abi.encodePacked("\x19\x01", domainSeparatorLocal(), structHash)
            );
    }

    function sign(
        bytes32 digest,
        uint256 pk
    ) internal pure returns (bytes memory sig) {
        (uint8 v, bytes32 r, bytes32 s) = vm.sign(pk, digest);
        sig = abi.encodePacked(r, s, v);
    }

    function test_payWithEIP712_USDC_DistributionMock() public {
        //Quote
        RecipientHandler.PriceQuote memory q = RecipientHandler.PriceQuote({
            quoteId: quoteId,
            paymentToken: USDC,
            seller: seller,
            price: price,
            cost: cost,
            serviceId: serviceId,
            expiry: nowTime + 365 days
        });
        bytes32 quoteStruct = keccak256(
            abi.encode(
                PRICEQUOTE_TYPEHASH,
                q.quoteId,
                q.paymentToken,
                q.seller,
                q.price,
                q.cost,
                q.serviceId,
                q.expiry
            )
        );
        bytes32 quoteDigest = hashTyped(quoteStruct);
        bytes memory sellerSig = sign(quoteDigest, sellerPk);

        // Order
        uint256 buyerNonce = handler.nextNonce(buyer);
        RecipientHandler.Order memory o = RecipientHandler.Order({
            buyer: buyer,
            payTo: address(handler),
            paymentToken: USDC,
            totalAmount: price,
            nonce: buyerNonce,
            quoteId: quoteId,
            serviceId: serviceId,
            deadline: nowTime + 1 days
        });

        bytes32 orderStruct = keccak256(
            abi.encode(
                ORDER_TYPEHASH,
                o.buyer,
                o.payTo,
                o.paymentToken,
                o.totalAmount,
                o.nonce,
                o.quoteId,
                o.serviceId,
                o.deadline
            )
        );
        bytes32 orderDigest = hashTyped(orderStruct);
        bytes memory buyerSig = sign(orderDigest, buyerPk);

        uint256 buyerBefore = IERC20(USDC).balanceOf(buyer);
        uint256 scBefore = IERC20(USDC).balanceOf(address(handler));

        uint256 netProfit = price - cost;
        uint256 commission = Math.mulDiv(netProfit, commissionRate, 10_000);
        uint256 sellerProfit = o.totalAmount - commission;

        vm.startPrank(buyer);
        handler.payWithEIP712(
            o,
            buyerSig,
            q,
            sellerSig,
            bytes(""), // permit2612
            bytes("") // permit2
        );
        vm.stopPrank();

        // handler
        assertEq(
            IERC20(USDC).balanceOf(buyer),
            buyerBefore - price,
            "buyer USDC not deducted"
        );
        assertEq(
            IERC20(USDC).balanceOf(address(handler)),
            scBefore + price - commission,
            "handler holds sellerProfit"
        );

        // MockDistribution
        assertEq(distribution.lastAmount(), commission, "commission mismatch");
        assertEq(
            distribution.lastBroker(),
            address(0),
            "broker should be zero"
        );

        //the test will be replace later
        //then use mapping to calculate amount of claim
        uint256 handlerUSDCBeforeClaim = IERC20(USDC).balanceOf(
            address(handler)
        );
        vm.prank(seller);
        handler.claim(USDC, sellerProfit);
        assertEq(
            IERC20(USDC).balanceOf(seller),
            sellerProfit,
            "seller did not receive profit"
        );
        assertEq(
            IERC20(USDC).balanceOf(address(handler)),
            handlerUSDCBeforeClaim - sellerProfit,
            "handler did not pay seller"
        );
    }

    function test_payWithEIP712_RevertWith_BuyerSigExpired() public {
        vm.startPrank(buyer);
        uint256 buyerNonce = handler.nextNonce(buyer);
        RecipientHandler.Order memory o = RecipientHandler.Order({
            buyer: buyer,
            payTo: address(handler),
            paymentToken: USDC,
            totalAmount: price,
            nonce: buyerNonce,
            quoteId: quoteId,
            serviceId: serviceId,
            deadline: nowTime + 1 days
        });
        bytes32 orderStruct = keccak256(
            abi.encode(
                ORDER_TYPEHASH,
                o.buyer,
                o.payTo,
                o.paymentToken,
                o.totalAmount,
                o.nonce,
                o.quoteId,
                o.serviceId,
                o.deadline
            )
        );
        bytes32 orderDigest = hashTyped(orderStruct);
        bytes memory buyerSig = sign(orderDigest, buyerPk);

        RecipientHandler.PriceQuote memory q = RecipientHandler.PriceQuote({
            quoteId: quoteId,
            paymentToken: USDC,
            seller: seller,
            price: price,
            cost: cost,
            serviceId: serviceId,
            expiry: nowTime + 365 days
        });
        vm.warp(nowTime + 2 days); //Expired
        vm.expectRevert(Errors.SigExpired.selector);
        handler.payWithEIP712(
            o,
            buyerSig,
            q,
            bytes(""), //sellerSig
            bytes(""), // permit2612
            bytes("") // permit2
        );
        vm.stopPrank();
    }

    function test_payWithEIP712_RevertWith_SellerSigExpired() public {
        vm.prank(buyer);
        RecipientHandler.PriceQuote memory q = RecipientHandler.PriceQuote({
            quoteId: quoteId,
            paymentToken: USDC,
            seller: seller,
            price: price,
            cost: cost,
            serviceId: serviceId,
            expiry: nowTime + 1 days
        });
        bytes32 quoteStruct = keccak256(
            abi.encode(
                PRICEQUOTE_TYPEHASH,
                q.quoteId,
                q.paymentToken,
                q.seller,
                q.price,
                q.cost,
                q.serviceId,
                q.expiry
            )
        );
        bytes32 quoteDigest = hashTyped(quoteStruct);
        bytes memory sellerSig = sign(quoteDigest, sellerPk);

        uint256 buyerNonce = handler.nextNonce(buyer);
        RecipientHandler.Order memory o = RecipientHandler.Order({
            buyer: buyer,
            payTo: address(handler),
            paymentToken: USDC,
            totalAmount: price,
            nonce: buyerNonce,
            quoteId: quoteId,
            serviceId: serviceId,
            deadline: nowTime + 10 days //Eusure Quote expire before Order
        });
        bytes32 orderStruct = keccak256(
            abi.encode(
                ORDER_TYPEHASH,
                o.buyer,
                o.payTo,
                o.paymentToken,
                o.totalAmount,
                o.nonce,
                o.quoteId,
                o.serviceId,
                o.deadline
            )
        );
        bytes32 orderDigest = hashTyped(orderStruct);
        bytes memory buyerSig = sign(orderDigest, buyerPk);

        vm.warp(nowTime + 2 days); //Expired
        vm.expectRevert(Errors.SigExpired.selector);
        handler.payWithEIP712(
            o,
            buyerSig,
            q,
            sellerSig,
            bytes(""), // permit2612
            bytes("") // permit2
        );
        vm.stopPrank();
    }

    function test_payWithEIP712_RevertWith_NotPayTo(address _payTo) public {
        vm.startPrank(buyer);
        RecipientHandler.PriceQuote memory q = RecipientHandler.PriceQuote({
            quoteId: quoteId,
            paymentToken: USDC,
            seller: seller,
            price: price,
            cost: cost,
            serviceId: serviceId,
            expiry: nowTime + 10 days
        });
        bytes32 quoteStruct = keccak256(
            abi.encode(
                PRICEQUOTE_TYPEHASH,
                q.quoteId,
                q.paymentToken,
                q.seller,
                q.price,
                q.cost,
                q.serviceId,
                q.expiry
            )
        );
        bytes32 quoteDigest = hashTyped(quoteStruct);
        bytes memory sellerSig = sign(quoteDigest, sellerPk);

        uint256 buyerNonce = handler.nextNonce(buyer);
        RecipientHandler.Order memory o = RecipientHandler.Order({
            buyer: buyer,
